    width: 3rem;
    height: 3rem;

    box-sizing: border-box;
    margin: 0;
    padding: 0;
    color: ${props => props.theme.text};
    background-color: ${props => props.theme.body};

    font-size: ${props => props.theme.fontxl};
    position: fixed;
    right: 1rem;
    bottom: 1rem;

    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;

    border-radius: 50%;
`;

const ScrollToTop = () => {
