import React from "react";
import styled from "styled-components";
import TypeWriter from "typewriter-effect";


const Title = styled.h2`
    font-size: ${props => props.theme.fontxxl};
    text-transform: capitalize;
    width: 80%;
    color: ${props => props.theme.text};
    align-self: flex-start;


span {
    text-transform: uppercase;
    font-family: "Akaya Telivigala", cursive;
}
.text-1 {
    color: blue;
}
.text-2 {
    color: orange;
}
.text-3 {
    color: red;
}
`;

const SubTitle = styled.h3`
    font-size: ${props => props.theme.fontlg};
    text-transform: capitalize;
    color: ${props => `rgba($(props.theme.text}, 0.6)`};
`;

const TypeWriterText = () => {
    return (
        <Title>
            Discover a new era of cool 
            <TypeWriter
            options={{
                autoStart: true,
                loop: true,
            }}
            onInit={(typewriter) => {
                typewriter.typeString('<span class="text-1">Hi, I am</span>')
                .pauseFor(2000)
                .deleteAll()
                .typeString('<span class="text-2">You, are</span>')
                .pauseFor(2000)
                .deleteAll()
                .typeString('<span class="text-3">He is</span>')
                .start();
            }}
            />
            <SubTitle>Bored of Shiba? Try Something New.</SubTitle>
        </Title>
    );
}

export default TypeWriterText;
